package kh.java.func;

public class Animal {
	private int hp;

	public Animal() {
		super();
		// TODO Auto-generated constructor stub
	}

	public Animal(int hp) {
		super();
		this.hp = hp;
	}

	/**
	 * @return the hp
	 */
	public int getHp() {
		return hp;
	}

	/**
	 * @param hp the hp to set
	 */
	public void setHp(int hp) {
		this.hp = hp;
	}
}
