mapper.select:
SELECT board_no, board_title, board_content, board_writer, read_count, reg_date
FROM board
WHERE board_no = #{boardNo}

mapper.update:
UPDATE board
SET board_title = #{boardTitle},
    board_content = #{boardContent}
WHERE board_no = #{boardNo}

UPDATE BOARD
SET READ_COUNT = READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}
